import React from 'react';


class NewItemForm extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            itemCount: 1,
            itemName: "",
        }

        this.submitForm = this.submitForm.bind(this)
        this.updateItemCount = this.updateItemCount.bind(this)
        this.updateItemName = this.updateItemName.bind(this)
    }

    updateItemCount(event) {
        this.setState({
            itemCount: event.target.value
        })
    }

    updateItemName(event) {
        this.setState({
            itemName: event.target.value
        })
    }

    submitForm(event) {
        event.preventDefault()

        let item = {
            number: this.state.itemCount,
            name: this.state.itemName,
            purchased:false
        }

        this.props.addItem(item)

        this.setState({
            itemCount: 1,
            itemName: ""
        })
    }

    render() {
        return (
            <form>
                <div className="field has-addons">
                  <div className="control">
                    <input
                        style={{ maxWidth : "6em"}}
                        className="input"
                        type="number"
                        placeholder="1"
                        min="1"
                        aria-label="number to purchase" />
                  </div>

                  <div className="control is-expanded">
                    <input
                        className="input"
                        type="text"
                        placeholder="Item To Buy"
                        aria-label="item to purchase" />
                  </div>

                  <div className="control">
                    <input
                        className="button is-info"
                        type="submit"
                        value="Add Item" />
                  </div>
                </div>
              </form>
        )
    }
}

export default NewItemForm
