import React from 'react';

class App extends React.Component {
  constructor(props){ 
    super(props)
    this.state = {
      items: [
        {
          name: "Broccoli",
          number: 3,
          purchased: false,
        },
        {
          name: "Bananas",
          number: 6,
          purchased: true,
        }
      ]
    }
  }
  render() {
    return (
      <section className="section">
        <div className="container is-fluid">
          <div className="columns">


      <div className="column is-half is-offset-one-quarter">
        <h1 className="title">Grocery List</h1>

        <form>
        <div className="field has-addons">
          <div className="control">
            <input
            style={{ maxWidth : "6em"}}
            className="input"
            type="number"
            placeholder="1"
            min="1"
            aria-label="number to purchase" />
          </div>

          <div className="control is-expanded">
            <input
            className="input"
            type="text"
            placeholder="Item To Buy"
            aria-label="item to purchase" />
          </div>

          <div className="control">
            <input
            className="button is-info"
            type="submit"
            value="Add Item" />
          </div>
        </div>
        </form>


        <div className="my-5" >
          <ul>
            {this.state.items.map((item, index) => (
              <li key={index}>
                <label 
                  className="checkbox"
                  style={{
                    textDecoration: item.purchased
                      ? "line-through"
                      : "none",
                  }}
                  >
                    <input checked={item.purchased} className="mr-3" type="checkbox" />
                    {item.name} <span className="has-text-weight-light">({item.number})</span>
                  </label>
              </li>
            ))}
          </ul>
        </div>


      </div>
      </div>
        </div>

      </section>
    );

  }

}

export default App;
